mapper.select:
	select
	
	from products p,brand b,producttype pt,productKeyword pk
	where p.brand_id=b.id and p.type_id=pt.id and p.id=pk.product_id  
	
    
		and p.type_id=#{typeId}
	
	
		and p.name like '%${name}%'
	
	
		and p.addtime between #{addtime} and #{addtime1}
	
	
		limit #{page},#{limit}

select 

from products p,brand b,producttype pt,productKeyword pk
where p.brand_id=b.id and p.type_id=pt.id and p.id=pk.product_id  and p.id = #{id,jdbcType=INTEGER}
